package com.youku.statistics;import java.io.BufferedReader;import java.io.FileInputStream;import java.io.FileNotFoundException;import java.io.FileOutputStream;import java.io.IOException;import java.io.InputStreamReader;import java.util.ArrayList;import android.content.Context;import com.baseproject.utils.Logger;/** *  * @author 张宇 * @create-time May 23, 2012 11:55:09 AM * @version $Id *  */public class StatisticsDataManager {	public final static String DATANAME = "vv_url";	private final static String sENTER = "\r\n";	public static boolean write(String s, Context context) {		boolean isWriteSuccess = false;		try {			FileOutputStream fos = context.openFileOutput(DATANAME,					Context.MODE_APPEND);			fos.write(s.getBytes());			fos.write(sENTER.getBytes());			fos.close();			isWriteSuccess = true;		} catch (FileNotFoundException e) {		} catch (IOException e) {		} catch (Exception e) {		}		return isWriteSuccess;	}	public static ArrayList<String> read(Context context) {		final ArrayList<String> arrayList = new ArrayList<String>();		try {			FileInputStream reader = context.openFileInput(DATANAME);			InputStreamReader inputReader = new InputStreamReader(reader);			BufferedReader bufferReader = new BufferedReader(inputReader);			String line;			while ((line = bufferReader.readLine()) != null) {				arrayList.add(line);			}			bufferReader.close();			inputReader.close();			reader.close();			context.deleteFile(DATANAME);		} catch (FileNotFoundException e) {			e.printStackTrace();		} catch (IOException e) {			e.printStackTrace();		} catch (Exception e) {			e.printStackTrace();		}		if (!arrayList.isEmpty()) {			return arrayList;		} else {			return null;		}	}}