package com.youku.player.ui.widget;import com.youku.android.player.R;import android.app.Activity;import android.content.Context;import android.view.animation.Animation;import android.view.animation.AnimationUtils;/** *  * @author 张宇 * @create-time Jan 10, 2012 12:05:39 PM * @version $Id *  */public class YoukuAnimation {	public static void activityOpen(Context context) {		((Activity) context).overridePendingTransition(				R.anim.yp_activity_open_enter, R.anim.yp_activity_open_exit);	}	public static void activityClose(Context context) {		((Activity) context).overridePendingTransition(				R.anim.yp_activity_close_enter, R.anim.yp_activity_close_exit);	}	public static Animation obtain(Context context, int id) {		if (context == null) {			return null;		}		final Animation a = AnimationUtils.loadAnimation(context, id);		return a;	}}