package com.youku.statistics;import java.util.ArrayList;import android.content.Context;/** *   用于离线统计数据操作 *   @author          张宇  *   @create-time     Jul 10, 2012   10:44:18 AM    *   @version         $Id * */public class OfflineStatistics {		/**	 * 发送离线vv	 */	public void sendVV(Context context){		final ArrayList<String> urls = StatisticsDataManager.read(context);		if (null != urls && !urls.isEmpty()) {			for (String s : urls) {				if (s.contains("danmu/add")) {					final StatisticsTask statisticsTask = new StatisticsTask(s, context,							true, false);					statisticsTask.execute();				} else {					final StatisticsTask statisticsTask = new StatisticsTask(s,							context);					statisticsTask.execute();				}			}		}	}}